import React from 'react';
import "../styles/background.css";
import { AwesomeButton } from "react-awesome-button";
import "react-awesome-button/dist/styles.css";
import 'react-awesome-button/dist/themes/theme-blue.css';
import Typical from 'react-typical';
import Robot from './robot';
import Particles from "reactparticles.js";
import "animate.css/animate.min.css";

import { css } from "@emotion/core";
import ClipLoader from "react-spinners/ClipLoader";

const override = css`
  display: block;
  margin: 0 auto;
  border-color: red;
`;


class Background extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      loading: true
    };
  }

	render(){
        return (

          <div className="sweet-loading">
          <ClipLoader
            css={override}
            size={150}
            color={"#123abc"}
            loading={this.state.loading}
          />

     <div className="backgroundWrapper">
            <div className="top-container">

             <div>
               <Typical steps={['Hello üëã', 1500]} loop={Infinity} wrapper="span" className={'myname'}  />
             </div>
             <br></br>
             <div>
               <Typical steps={['', 1500,'I am Kanyarut Pornamnuay',4000]}  wrapper="span" className={'myname'}  />
             </div>
             <br></br>
              <div>
               <Typical steps={['', 1500,'', 2000,'I am a Full Stack Web Developer üßê', 4000, 'Front End Developer ü§ì', 2000,'Back End Developer üòé', 2000,]} loop={Infinity} wrapper="span" className={'jobTitle'}  />
             </div>

             <br></br>
          <AwesomeButton><a href="#aboutMe" style={{color:'white'}}>View My Work</a></AwesomeButton>
            <br></br>



    <Particles
    id="config-1"
    config="particles/config-1.json"
    style={{
      width: "100%",
      height: "100%",
      opacity: "50px",
      color: {
        value: "#00FFFF"
       },
       line_linked: {
        color: {
        value: "#00FFFF"
        }
     },
    }}
    className="particles-class-name"
  />

   <Robot/> 


   </div>
  </div>
  </div>

       );
    }
 };
export default Background;
