import React from 'react';
import {Form,Col} from 'react-bootstrap'
import ScrollAnimation from 'react-animate-on-scroll';

class Contact extends React.Component {
	render(){

        return (
<div id="contact" style={{backgroundColor: '#c6dddd', height: '100vh', padding:'10px'}}>
<ScrollAnimation animateIn='wobble'initiallyVisible={true}>
<h1>Drop Me a Line</h1><i class="fa fa-comments-o" aria-hidden="true" style={{fontSize:'30px' ,paddingRight:'10px' }}></i>
</ScrollAnimation>



<div className="col-md-2 mb-md-0 p-md-4"></div>

  <div className="col-md-5 p-4 pl-md-4 shadow-lg p-3 mb-5 bg-white rounded" style={{marginTop: '10%', marginBottom: '10%'}} >

   <div >

   <ScrollAnimation animateIn='flipInY'animateOut='flipOutY'>
   <h1>Drop Me a Line</h1>
   </ScrollAnimation>
   <Form controlId="formBasicEmail" action="mailto:benbaba2525@gmail.com" method="post" enctype="text/plain">

   <Form.Group controlId="exampleForm.ControlInput1">
    <Form.Label>Email address</Form.Label>
    <Form.Control type="email" placeholder="name@example.com" />
  </Form.Group>

  <br />
  <Form.Control type="text" placeholder="Normal text" />
  <br />

    <Form.Group controlId="exampleForm.ControlTextarea1">
    <Form.Label>Example textarea</Form.Label>
    <Form.Control as="textarea" rows={3} />
  </Form.Group>


</Form>
 </div>

  </div>





</div>







            );
        }
     };
    export default Contact;
