import React from 'react';
import myPicture from '../img/mypicture.jpeg'


class About extends React.Component {


	render(){
        const styles= 
        {
          container:{
            background: 'black',
            top: 0,
            left: 0,
            zIndex: '99',
            opacity: 0.9,
            display:'flex',
            alignItems:'center',
            width: '100%',
            fontFamily:'Lobster',
          }
        };
        return (


<div style={style.comtainer}>
<h1>About Me</h1>
    <img
			className={"img-fluid " + this.props.fadeIn}
			src={myPicture}
			alt=""
			style={{
					borderRadius: 50 + "%",
					height: 300 + "px",
					width: 300 + "px"
					}}
	/>
</div>








            );
        }
     };
    export default About;
