import React from 'react';
import "../styles/navBar.css";
import "animate.css/animate.min.css";
import ScrollAnimation from 'react-animate-on-scroll';


class NavBar extends React.Component {
    constructor(props){
      super(props);
      this.state={
        menuOpen:false,
      }
    }

    handleMenuClick() {
      this.setState({menuOpen:!this.state.menuOpen});
    }


    render(){
      const styles= 
        {
          container:{
            position: 'absolute',
            top: 0,
            left: 0,
            zIndex: '99',
            opacity: 0.9,
            display:'flex',
            alignItems:'center',
            width: '100%',
            height:'8%',
            color: 'white',
            fontFamily:'Lobster',
          },
          logo: {
            margin: '0 auto',
          },
          body: {
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            width: '100vw',
            height: '100vh',
            filter: this.state.menuOpen ? 'blur(2px)':null,
            transition: 'filter 0.5s ease',
          },
        }
      const menu = ['Home','About','Skills','Portfolio','Contact']
      // const menuItems = menu.map((val,index)=>{
      //   return (
      //     <MenuItem 
      //       key={index} 
      //       delay={`${index * 0.1}s`}
      //       onclick = {menu[0]}><a href="#aboutMe" style={{color:'white'}}>{menu[0]}</a>

      //       </MenuItem>)

      // });

      return(
        <div>
          <div style={styles.container}>
            <MenuButton open={this.state.menuOpen} onClick={()=>this.handleMenuClick()} color='white'/>
            <div style={styles.logo}><a href="#aboutMe" style={{color:'white'}}>**KP**</a></div>
          </div>
          <Menu open={this.state.menuOpen}>

          <ScrollAnimation animateIn='bounceInRight' animateOut='bounceOutLeft'>
            <MenuItem>
          <a href="#aboutMe" style={{color:'white'}}>  {menu[0]}</a>
            </MenuItem>

            <MenuItem>
          <a href="#aboutMe" style={{color:'white'}}>  {menu[1]}</a>
            </MenuItem>
          </ScrollAnimation>




          </Menu>

        </div>
      )
    }
  }

  /* MenuItem.jsx*/
  class MenuItem extends React.Component{
    constructor(props){
      super(props);
      this.state = {
        hover:false,
      }
    }

    handleHover(){
      this.setState({hover:!this.state.hover});
    }

    render(){
      const styles={
        container: {
          opacity: 0,
          animation: '1s appear forwards',
          animationDelay:this.props.delay,
        },
        menuItem:{
          fontFamily:`'Open Sans', sans-serif`,
          fontSize: '1.2rem',
          padding: '1rem 0',
          margin: '0 5%',
          cursor: 'pointer',
          color: this.state.hover? 'gray':'#fafafa',
          transition: 'color 0.2s ease-in-out',
          animation: '0.5s slideIn forwards',
          animationDelay:this.props.delay,

        },
        line: {
          width: '90%',
          height: '1px',
          background: 'gray',
          margin: '0 auto',
          animation: '0.5s shrink forwards',
          animationDelay:this.props.delay,

        }
      }
      return(
        <div style={styles.container}>

          <div 
            style={styles.menuItem} 
            onMouseEnter={()=>{this.handleHover();}} 
            onMouseLeave={()=>{this.handleHover();}}
            onClick={this.props.onClick}
          >
            {/* {this.props.children} */}
          </div>
        <div style={styles.line}/>
      </div>  
      )
    }
  }

  /* Menu.jsx */
  class Menu extends React.Component {
    constructor(props){
      super(props);
      this.state={
        open: this.props.open? this.props.open:false,
      }
    }

    componentWillReceiveProps(nextProps){
      if(nextProps.open !== this.state.open){
        this.setState({open:nextProps.open});
      }
    }

    render(){
      const styles={
        container: {
          position: 'absolute',
          top: 0,
          left: 0,
          height: this.state.open? '100%': 0,
          width: '100vw',
          display: 'flex',
          flexDirection: 'column',
          background: '#001a1a',
          opacity: 0.95,
          color: '#fafafa',
          transition: 'height 0.3s ease',
          zIndex: 2,
        },
        menuList: {
          paddingTop: '15rem',
        }
      }
      return(
        <div style={styles.container}>
          {
            this.state.open?
              <div style={styles.menuList}>
                {this.props.children}
              </div>:null
          }
        </div>
      )
    }
  }

  /* MenuButton.jsx */
  class MenuButton extends React.Component {
    constructor(props){
      super(props);
      this.state={
        open: this.props.open? this.props.open:false,
        color: this.props.color? this.props.color:'black',
      }
    }

    componentWillReceiveProps(nextProps){
      if(nextProps.open !== this.state.open){
        this.setState({open:nextProps.open});
      }
    }

    handleClick(){
    this.setState({open:!this.state.open});
    }

    render(){
      const styles = {
        container: {
            height: '150px',
            width: '150px',
            display:'flex',
            flexDirection: 'column',
            justifyContent: 'center',
            alignItems: 'center',
            cursor: 'pointer',
            padding: '4px',  
        },
        line: {
            height: '4px',
            width: '35px',
            background: this.state.color,
            transition: 'all 0.2s ease',
        },
        lineTop: {
            transform: this.state.open ? 'rotate(45deg)':'none',
            transformOrigin: 'top left',
            marginBottom: '8px',
        },
        lineMiddle: {
          opacity: this.state.open ? 0: 1,
          transform: this.state.open ? 'translateX(-16px)':'none',
        },
        lineBottom: {
            transform: this.state.open ? 'translateX(-1px) rotate(-45deg)':'none',
            transformOrigin: 'top left',
            marginTop: '8px',
        },       
      }
      return(
        <div style={styles.container} 
          onClick={this.props.onClick ? this.props.onClick: 
            ()=> {this.handleClick();}}>
          <div style={{...styles.line,...styles.lineTop}}/>
          <div style={{...styles.line,...styles.lineMiddle}}/>
          <div style={{...styles.line,...styles.lineBottom}}/>
        </div>
      )
    }


  };




  export default NavBar;
